// src/App.js

import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
        locales: [],
    };
  }

  componentDidMount() {
    fetch('/api/v1/locales')
        .then(res => res.json())
        .then(data => this.setState({ locales: data.content }));
  }

  render() {
    const { locales } = this.state;

    return (
        <ul>
        {locales.map(locale =>
            <li key={locale.id}>
                {locale.name}
            </li>
        )}
        </ul>
    );
  }
}

export default App;
